def icon_database(name: str = None) -> str | dict:    data = {'testk':    './icons/tk_smal.png',            'widget':   './icons/tk_smal.png',            'home':     './icons/home.png',            'music':    './icons/music.png',            'info':     './icons/info.png',            'yes':      './icons/yes.png',            'no':       './icons/no.png',            'cursor':   './icons/cursor.png'}    if name in data:        return data[name]    return data