from widgets import tesButtonfrom tkinter import Frame, Label, Topleveldef widget_settings() -> dict:	settings = {		'width': 180,		'height': 40,		'radius': 11,		'backgroundidx': 2,		'bordercoloridx': 3,		'fontsize': 16,		'background': 'green',		'bordercolor': 'black',		'text': 'OK',		'textcolor': 'white',		'fonttype': 'Verdana',		'style': 'simple'}	return settingsclass tesShowInfo(Toplevel):	def __init__(self,				 container = None,				 title: str = 'ShowInfo - Action',				 message: str = 'This process has been completed!'):		Toplevel.__init__(self, container, background='#585858')		self.geometry('600x200')		self.minsize(400, 150)		self.title(title)		self.create_UI(message)		self.wait_window()	def create_UI(self, message):		self.create_frames()		self.insert_label(message)		self.insert_widgets()	def create_frames(self):		self.menu = Frame(self, background='#424242')		self.menu.pack(side='bottom', fill='x')	def insert_label(self, message):		msg = Label(self, text=message, bg='#585858', font=('Arial', 15), wraplength=400, fg='white')		msg.pack(expand=True)	def insert_widgets(self):		ok = tesButton(self.menu, **widget_settings(), command=lambda: self.destroy())		ok.pack(side='right', padx=(8, 5), pady=8)