from widgets import tesButtonfrom tkinter import Frame, Label, Topleveldef widget_settings() -> dict:	settings = {		'width': 180,		'height': 38,		'radius': 11,		'backgroundidx': 2,		'bordercoloridx': 3,		'fontsize': 16,		'background': 'green',		'bordercolor': 'black',		'text': 'OK',		'textcolor': 'white',		'fonttype': 'Verdana',		'style': 'simple'}	return settingsclass FrameControlPanel(Frame):	def __init__(self, container: Toplevel):		Frame.__init__(self, container, background='#424242')		self.pack(side='bottom', fill='x')class tesShowInfo(Toplevel):	def __init__(self,				 title: str = 'ShowInfo - Action',				 message: str = 'This process has been completed!'):		Toplevel.__init__(self, background='#585858')		self.geometry('600x200')		self.minsize(400, 150)		self.title(title)		self.panel = FrameControlPanel(self)		self.create_UI(message)		self.wait_window()	def create_UI(self, message) -> None:		self.insert_label(message)		self.insert_buttons()	def insert_label(self, message) -> None:		Label(self, text=message, bg='#585858', font=('Arial', 15), wraplength=400, fg='white').pack(expand=True)	def insert_buttons(self) -> None:		tesButton(self.panel, **widget_settings(), command=lambda: self.destroy()).pack(side='right', padx=(8, 5), pady=8)