from tkinter import Toplevel, Frame, Label, PhotoImageimport webbrowserdefault_data = {'Created': 'Lukas',                'Country': 'Poland',                'Contact': 'linuxieducation@gmail.com',                'YouTube': ['Linux Education', 'https://www.youtube.com/channel/UC_tx8bMzfLU-hDml6PzSTqg'],                'GitHub':  ['LinuxEducation', 'https://github.com/LinuxEducation'],                'Patronite': 'Support the Author'}class AppInformation(Toplevel):    def __init__(            self,            container = None,            window_x: int = 700,            window_y: int = 300,            title: str = 'Aplication Information',            logo: str = './images/Tk-logo-blue.png',            data: dict = default_data,            url: str = None,            style: str = 'Vertical'):        Toplevel.__init__(self, container, background='#585858')        self.iconphoto(False, PhotoImage(file='./icons/tesTk_window3.png'))        self.title(title)        self.geometry(f'{window_x}x{window_y}')        self.create_frames(style)        self.insert_logo(logo)        self.insert_data(data, url)    def create_frames(self, style):        self.logo_frame = Frame(self, background='#424242')        self.data_frame = Frame(self, background=self['background'], height=10)        if style == 'Horizontal':            self.logo_frame.pack(fill='both', expand=True)            self.data_frame.pack(fill='both', expand=True, padx=(5, 0))        if style == 'Vertical':            self.logo_frame.pack(side='left', fill='both', expand=True)            self.data_frame.pack(side='left', expand=True, anchor='w', padx=(5, 0))    def insert_logo(self, logo):        self.logo = PhotoImage(file=logo).subsample(2,2)        logo = Label(            self.logo_frame,            background='#424242',            image=self.logo,            anchor='e')        logo.pack(side='right', padx=(0, 3))    def insert_data(self, data, url, i=0) -> None:        for key, value in data.items():            data_0 = Label(self.data_frame, text=f'{key}: ', **self.data_settings())            data_0.configure(font=(*self.data_settings().get('font'), 'bold'))            data_0.grid(row=i, column=0, sticky='w')            if isinstance(value, list):                url = value[-1] if 'http' in value[-1] else None                value = value[0]            data_1 = Label(self.data_frame, text=f'{value:}', **self.data_settings())            data_1.configure(font=(*self.data_settings().get('font'), 'italic'))            data_1.grid(row=i, column=1, padx=(3, 0), sticky='w')            if url:                if 'YouTube' in key:                    color = '#03A9F4'                if 'GitHub' in key:                    color = '#4CAF50'                data_1.url = url                data_1.configure(font=(*self.data_settings().get('font'), 'italic', 'underline'), foreground=color)                data_1.bind('<Button-1>', self.url_open)                url = None            i += 1    def url_open(self, event) -> None:        url = event.widget.url        webbrowser.open_new(url)    def tkinter_data_settings(self) -> dict:        settings = {'background': self.data_frame['background'],                    'foreground': 'white',                    'font': ('Havletica', 12)}        return settings    data_settings = tkinter_data_settings