from src.widget_composition import WidgetCompositionfrom tkinter import Textimport mathclass tesText(WidgetComposition):    message = "tesTk is based on the tkinter library. The first layer of each widget is a canvas, so you decide how the widget will look and change. You can create animations, draw your own controls, change widget shapes. You can also easily combine multiple widgets into one. Tkinter Canvas allows you to add any widgets you want: Frame, Button, Entry or Text, Images. Each pixel on the canvas, each object can be tagged by giving it a name, allowing you to change these objects or remove them."    def __init__(self, container, **kw):        super().__init__(container, **kw)        self.draw_widget()        self._insert_tkinter_frame_widget()        self.insert_widget()    def draw_widget(self) -> None:        WidgetComposition.draw_widget(self)    def calculate_max_point(self, offset: int = 15) -> int:        AB = math.sqrt((self.radius//4) * (self.radius//4) *2)        max_position = (self.radius//2 - AB) *2        return max_position + offset    def insert_widget(self) -> None:        self.tkintertext = Text(self.tkinterframe, background=self.background, highlightthickness=0, borderwidth=0, wrap='word')        self.tkintertext.pack()        self.tkintertext.insert(0.0, tesText.message)